import React, { useState } from "react";
import "./styles.css";

const regimens = [
  "CHOP: Protocol for Non Hodgkin Lymphoma",
  "AC - Regimen for Non-Metastatic, Locally-Advanced Breast Cancer. Before or after Taxol",
  "CMF-Breast Cancer Regimen",
  "COP - Regimen for Non-Metastatic Non Hodgkin Lymphoma",
  "Carboplatin + Taxol Regimen",
  "Single-Agent Cyclophosphamide - Breast Cancer Regimen",
  "Single-Agent Doxorubicin - Breast Cancer Regimen 20 mg/m2",
];

export default function DropDownMenu() {
  const [isOpen, setIsOpen] = useState(false);
  const [selectedOption, setSelectedOption] = useState("none");

  const toggleDropdown = () => {
    setIsOpen(!isOpen);
  };

  const handleOptionSelect = (option) => {
    setSelectedOption(option);
    setIsOpen(false);
  };

  return (
    <div className="dropdown-container">
      <div
        className={`dropdown-header ${isOpen ? "open" : ""}`}
        onClick={toggleDropdown}
      >
        <span>
          {selectedOption === "none" ? "Select an option" : selectedOption}
        </span>
        <span className={`arrow ${isOpen ? "up" : "down"}`}>&#9660;</span>
      </div>
      {isOpen && (
        <div className="dropdown-list">
          {regimens.map((regimen, index) => (
            <div
              key={index}
              className="dropdown-option"
              onClick={() => handleOptionSelect(regimen)}
            >
              {regimen}
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
