import React from "react";
import "./style.css";

function Table({ data, selectedOption, checkedItems, onCheckboxChange }) {
  return (
    <table className="medications-table">
      <thead>
        <tr>
          <th className="checkboxCell"></th>
          <th style={{ width: "240px" }}>Mediaction</th>
          <th style={{ width: "100px" }}>Dose</th>
          <th style={{ width: "100px" }}></th>
          <th style={{ width: "160px" }}>Route</th>
          <th>Instructions</th>
        </tr>
      </thead>
      <tbody>
        {selectedOption &&
          data[selectedOption]?.map((item, index) => (
            <tr key={index}>
              <td className="checkboxCell">
                <label class="container">
                  <input
                    type="checkbox"
                    checked={checkedItems[index] || false}
                    onChange={() => onCheckboxChange(index, item)}
                  />
                  <span class="checkmark"></span>
                </label>
              </td>
              <td>{item.Medication}</td>
              <td>{item.Dose}</td>
              <td>
                {item.doseReduction !== null ? item.doseReduction : ""}
              </td>{" "}
              <td>{item.Route}</td>
              <td>{item.Instructions}</td>
              {/* Display dose reduction or "-" if null */}
              {/* {Object.values(item).map((value, colIndex) => (
                <td key={colIndex}>{value} </td>
              ))} */}
            </tr>
          ))}
      </tbody>
    </table>
  );
}

export default Table;
