// Table component
import { useEffect, useState } from "react";

export default function Table({
  data,
  selectedOption,
  checkedItems,
  onCheckboxChange,
  setCheckedItems,
}) {
  const [tableData, setTableData] = useState(data[selectedOption] || []);

  useEffect(() => {
    setTableData(data[selectedOption] || []);
  }, [selectedOption, data]);

  const handleCheckboxChange = (index, item) => {
    const updatedCheckedItems = { ...checkedItems };
    updatedCheckedItems[index] = !updatedCheckedItems[index];

    if (!updatedCheckedItems[index]) {
      onCheckboxChange(null); // Pass null if unchecked
    } else {
      onCheckboxChange(item); // Pass item if checked
    }

    setCheckedItems(updatedCheckedItems);
  };

  return (
    <table>
      <thead>
        <tr>
          <th></th>
          <th>Medication</th>
          <th>Dose</th>
          <th>Route</th>
          <th>Instructions</th>
        </tr>
      </thead>
      <tbody>
        {tableData.map((item, index) => (
          <tr key={index}>
            <td>
              <input
                type="checkbox"
                checked={checkedItems[index] || false}
                onChange={() => handleCheckboxChange(index, item)}
              />
            </td>
            <td>{item.Medication}</td>
            <td>{item.Dose}</td>
            <td>{item.Route}</td>
            <td>{item.Instructions}</td>
          </tr>
        ))}
      </tbody>
    </table>
  );
}
