  },
  {
    Medication: "SODIUM CHLORIDE",
    Dose: 1000,
    Route: "Intravenous",
    Instructions: "Once 60 minutes prior to chemotherapy",
  },
];

export default function RegimenDetails() {
  const refContainer = useRef();
  const [dimensions, setDimensions] = useState({
    width: 0,
    height: 0,
  });
  useEffect(() => {
    if (refContainer.current) {
      setDimensions({
        width: refContainer.current.offsetWidth,
        height: refContainer.current.offsetHeight,
      });
    }
  }, []);

  return (
    <>
      <div className="cycles">
        <span className="heading">Cycles</span>
        <div className="row">
          <input type="radio" />
          <span>Every</span>
        </div>
        <div className="row">
          <input type="radio" />
          <span>Day</span>
        </div>
        <button>Check</button>
      </div>
      <div className="medications">
        <span className="heading">Mediactions</span>
        <div className="premediaction">
          <div className="premediactionTable">
            <p>PreMediactions</p>
            <div className="prebuttons">
              <button className="edit">Edit</button>
              <button className="del">Delete</button>
            </div>
          </div>
          <Table data={data} />
        </div>
        <div
          className="chemotherapy"
          style={{ top: premedicationHeight + 500 }}
        >
          <p>ChemoTherapy</p>
        </div>
      </div>
      <div className="notes">
        <span className="heading">Physician Notes</span>
        <input type="text" placeholder="Add your notes here..." />
      </div>
    </>
  );
}
