import Table from "components/Table/Table";
import "./style.css";
// import MiniDropMenu from "components/MiniDropMenu/MiniDropMenu";
import { useEffect, useState } from "react";

const premedicationData = {
  "COP - Regimen for Non-Metastatic Non Hodgkin Lymphoma": [
    {
      Medication: "SODIUM CHLORIDE",
      Dose: 1000,
      Route: "Intravenous",
      Instructions: "Once prior to chemotherapy",
    },
    {
      Medication: "Ondansetron",
      Dose: 8,
      Route: "Oral",
      Instructions: "Once 60 minutes prior to chemotherapy",
    },
  ],
  "AC - Regimen for Non-Metastatic, Locally-Advanced Breast Cancer. Before or after Taxol":
    [
      {
        Medication: "SODIUM CHLORIDE",
        Dose: 100,
        Route: "Intravenous",
        Instructions: "Once prior to chemotherapy",
      },
      {
        Medication: "Dexamethasone",
        Dose: 16,
        Route: "Oral",
        Instructions: "Once 60 minutes prior to chemotherapy",
      },
      {
        Medication: "Ondansetron",
        Dose: 8,
        Route: "Intravenous",
        Instructions: "Once 60 minutes prior to chemotherapy",
      },
    ],
  "CHOP: Protocol for Non Hodgkin Lymphoma": [
    {
      Medication: "SODIUM CHLORIDE",
      Dose: 100,
      Route: "Oral",
      Instructions: "Once prior to chemotherapy",
    },
    {
      Medication: "Ondansetron",
      Dose: 8,
      Route: "Oral",
      Instructions: "Once 60 minutes prior to chemotherapy",
    },
  ],
  "CMF-Breast Cancer Regimen": [
    {
      Medication: "SODIUM CHLORIDE",
      Dose: 1000,
      Route: "Intravenous",
      Instructions: "Once  minutes prior to chemotherapy",
    },
    {
      Medication: "Dexamethasone",
      Dose: 16,
      Route: "Oral",
      Instructions: "Once 60 minutes prior to chemotherapy",
    },
    {
      Medication: "Ondansetron",
      Dose: 8,
      Route: "Oral",
      Instructions: "Once 60 minutes prior to chemotherapy",
    },
  ],
};
const chemotherapyData = {
  "COP - Regimen for Non-Metastatic Non Hodgkin Lymphoma": [
    {
      Medication: "Prednisone",
      Dose: 100,
      Route: "Oral",
      Instructions: "Daily x 5 days. 1st dose 60 minutes prio to chemotherapy.",
    },
    {
      Medication: "Vincristine",
      Dose: 1.4,
      Route: "Intravenous",
      Instructions: "IV Push over 1-2 minutes",
    },
    {
      Medication: "Cyclophosphamide",
      Dose: 750,
      Route: "Intravenous",
      Instructions: "IV Push over 1-2 hours",
    },
  ],
  "AC - Regimen for Non-Metastatic, Locally-Advanced Breast Cancer. Before or after Taxol":
    [
      {
        Medication: "Doxorubici",
        Dose: 60,
        Route: "Intravenous",
        Instructions: "IV Push over 15 minutes",
      },
      {
        Medication: "Cyclophosphamide",
        Dose: 600,
        Route: "Intravenous",
        Instructions: "Infuse over 1-2 hours",
      },
    ],
  "CHOP: Protocol for Non Hodgkin Lymphoma": [
    {
      Medication: "Prednisone",
      Dose: 100,
      Route: "Oral",
      Instructions: "Daily x 5 days. 1st dose 60 minutes prio to chemotherapy",
    },
    {
      Medication: "Doxorubicin",
      Dose: 50,
      Route: "Oral",
      Instructions: "IV Push over 15 minutes",
    },
    {
      Medication: "Vincristine",
      Dose: 1.4,
      Route: "Oral",
      Instructions: "IV Push over 1-2 minutes",
    },
    {
      Medication: "Cyclophosphamide",
      Dose: 750,
      Route: "Oral",
      Instructions: "IV Push over 1-2 hours",
    },
  ],
  "CMF-Breast Cancer Regimen": [
    {
      Medication: "Fluorouracil",
      Dose: 600,
      Route: "Intravenous",
      Instructions: "IV push over 10 minutes",
    },
    {
      Medication: "Methotrexate",
      Dose: 40,
      Route: "Oral",
      Instructions: "IV push over 10 mg/min",
    },
    {
      Medication: "Cyclophosphamide",
      Dose: 600,
      Route: "Oral",
      Instructions: "Infuse over 1-2 hours",
    },
  ],
};
// const cyclesData = {
//   "COP - Regimen for Non-Metastatic Non Hodgkin Lymphoma": [
//     { NumOfCycles: 3, CycleCount: 2 },
//   ],
//   "AC - Regimen for Non-Metastatic, Locally-Advanced Breast Cancer. Before or after Taxol":
//     [{ NumOfCycles: 8, CycleCount: 5 }],
//   "CHOP: Protocol for Non Hodgkin Lymphoma": [
//     { NumOfCycles: 2, CycleCount: 3 },
//   ],
//   "CMF-Breast Cancer Regimen": [{ NumOfCycles: 4, CycleCount: 4 }],
// };

// const Weeks = ["None", "1", "2", "3", "4", "5", "6"];

// const Cycles = [1, 2, 3, 4, 5, 6, 7, 8];

// const Days = Array.from({ length: 28 }, (_, index) => index + 1);

// const fieldsNum = [1, 2, 3];

export default function RegimenDetails({ selectedOption }) {
  const [notes, setNotes] = useState("Add your notes here...");
  const [checkedRows, setCheckedRows] = useState([]);
  const [newRegimenDetails, setNewRegimenDetails] = useState({
    regimenName: selectedOption,
    preMedication: [],
    chemoTherapy: [],
    physicianNotes: notes,
  });

  useEffect(() => {
    setNotes("Add your notes here...");
    setNewRegimenDetails({
      ...newRegimenDetails,
      regimenName: selectedOption,
    });
  }, [selectedOption]);

  const handleCheckboxChange = (index) => {
    const isChecked = checkedRows.includes(index);
    setCheckedRows((prevCheckedRows) =>
      isChecked
        ? prevCheckedRows.filter((rowIndex) => rowIndex !== index)
        : [...prevCheckedRows, index]
    );
  };

  const handleDeleteCheckedRows = () => {
    const updatedPreMedication = newRegimenDetails.preMedication.filter(
      (_, index) => !checkedRows.includes(index)
    );
    const updatedChemoTherapy = newRegimenDetails.chemoTherapy.filter(
      (_, index) => !checkedRows.includes(index)
    );

    setNewRegimenDetails({
      ...newRegimenDetails,
      preMedication: updatedPreMedication,
      chemoTherapy: updatedChemoTherapy,
    });

    setCheckedRows([]);
  };

  const handleNextClick = () => {
    const newRegimenDetails = {
      regimenName: selectedOption,
      preMedication: premedicationData[selectedOption] || [],
      chemoTherapy: chemotherapyData[selectedOption] || [],
      physicianNotes: notes,
    };

    setNewRegimenDetails(newRegimenDetails);

    setNewRegimenDetails((prevRegimenDetails) => {
      console.log(prevRegimenDetails);
      return prevRegimenDetails;
    });
  };

  return (
    <div className="regimen-detail">
      <div className="medications">
        <span className="heading">Medications</span>
        <div className="pre-medication">
          <div className="pre-medication-header">
            <p className="table-name">PreMedications</p>
            <div className="buttons-container">
              <button className="edit btn">Edit</button>
              <button className="del btn" onClick={handleDeleteCheckedRows}>
                Delete Checked Rows
              </button>
            </div>
          </div>
          <Table
            data={premedicationData}
            selectedOption={selectedOption}
            checkedRows={checkedRows}
            handleCheckboxChange={handleCheckboxChange}
          />
        </div>
        <div className="chemotherapy">
          <div className="chemotherapy-header">
            <p className="table-name">Chemotherapy</p>
            <div className="buttons-container">
              <button className="dose btn">Change Dose</button>
              <button className="edit btn">Edit</button>
              <button className="del btn" onClick={handleDeleteCheckedRows}>
                Delete Checked Rows
              </button>
            </div>
          </div>
          <Table
            data={chemotherapyData}
            selectedOption={selectedOption}
            checkedRows={checkedRows}
            handleCheckboxChange={handleCheckboxChange}
          />
        </div>
      </div>
      <div className="notes">
        <span className="heading">Physician Notes</span>
        <textarea
          value={notes}
          className="notes-area"
          onChange={(e) => setNotes(e.target.value)}
        ></textarea>
        <button className="next" onClick={handleNextClick}>
          Next
        </button>
      </div>
    </div>
  );
}
