  //   });
  // };

  // const deleteRegimenItem = (deletedItem) => {
  //   const newData = {
  //     ...Data,
  //     preMedication: {
  //       ...Data.preMedication,
  //       [selectedOption]: Data.preMedication[selectedOption].filter(
  //         (item) => item.Medication !== deletedItem.Medication
  //       ),
  //     },
  //     chemoTherapy: {
  //       ...Data.chemoTherapy,
  //       [selectedOption]: Data.chemoTherapy[selectedOption].filter(
  //         (item) => item.Medication !== deletedItem.Medication
  //       ),
  //     },
  //   };
  //   setData(newData);
  //   setRegimenDetails({
  //     ...regimenDetails,
  //     preMedication: newData.preMedication[selectedOption] || [],
  //     chemoTherapy: newData.chemoTherapy[selectedOption] || [],
  //   });
  // };

  const updateRegimenData = (updatedItem) => {
    const newData = {
      ...localData,
      preMedication: {
        ...localData.preMedication,
        [selectedOption]: localData.preMedication[selectedOption].map((item) =>
          item.Medication === updatedItem.Medication ? updatedItem : item
        ),
      },
      chemoTherapy: {
        ...localData.chemoTherapy,
        [selectedOption]: localData.chemoTherapy[selectedOption].map((item) =>
          item.Medication === updatedItem.Medication ? updatedItem : item
        ),
      },
    };
    setLocalData(newData);
    setRegimenDetails({
      ...regimenDetails,
      preMedication: newData.preMedication[selectedOption] || [],
      chemoTherapy: newData.chemoTherapy[selectedOption] || [],
    });
  };

  const updateRegimenDoseReduction = (updatedItem) => {
    const newData = {
      ...localData,
      chemoTherapy: {
        ...localData.chemoTherapy,
        [selectedOption]: localData.chemoTherapy[selectedOption].map((item) =>
          item.Medication === updatedItem.Medication ? updatedItem : item
        ),
      },
    };
    setLocalData(newData);
    setRegimenDetails({
      ...regimenDetails,
      preMedication: newData.preMedication[selectedOption] || [],
      chemoTherapy: newData.chemoTherapy[selectedOption] || [],
    });
  };

  const deleteRegimenItem = (deletedItem) => {
    const newData = {
      ...localData,
      preMedication: {
        ...localData.preMedication,
        [selectedOption]: localData.preMedication[selectedOption].filter(
          (item) => item.Medication !== deletedItem.Medication
