// RegimenDetails.jsx
import React, { useState, useEffect } from "react";
import Table from "./Table";
import "./style.css";

const premedicationData = {
  // Your premedication data
};

const chemotherapyData = {
  // Your chemotherapy data
};

export default function RegimenDetails({ selectedOption }) {
  const [notes, setNotes] = useState("Add your notes here...");
  const [checkedRows, setCheckedRows] = useState([]);
  const [newRegimenDetails, setNewRegimenDetails] = useState({
    regimenName: selectedOption,
    preMedication: [],
    chemoTherapy: [],
    physicianNotes: notes,
  });

  useEffect(() => {
    setNotes("Add your notes here...");
    setNewRegimenDetails({
      ...newRegimenDetails,
      regimenName: selectedOption,
    });
  }, [selectedOption]);

  const handleCheckboxChange = (index) => {
    const isChecked = checkedRows.includes(index);
    setCheckedRows((prevCheckedRows) =>
      isChecked
        ? prevCheckedRows.filter((rowIndex) => rowIndex !== index)
        : [...prevCheckedRows, index]
    );
  };

  const handleDeleteCheckedRows = () => {
    const updatedPreMedication = newRegimenDetails.preMedication.filter(
      (_, index) => !checkedRows.includes(index)
    );
    const updatedChemoTherapy = newRegimenDetails.chemoTherapy.filter(
      (_, index) => !checkedRows.includes(index)
    );

    setNewRegimenDetails({
      ...newRegimenDetails,
      preMedication: updatedPreMedication,
      chemoTherapy: updatedChemoTherapy,
    });

    setCheckedRows([]);
  };

  const handleNextClick = () => {
    // Handle next click
  };

  return (
    <div className="regimen-detail">
      <div className="medications">
        <span className="heading">Medications</span>
        <div className="pre-medication">
          <div className="pre-medication-header">
            <p className="table-name">PreMedications</p>
            <div className="buttons-container">
              <button className="edit btn">Edit</button>
              <button className="del btn" onClick={handleDeleteCheckedRows}>
                Delete Checked Rows
              </button>
            </div>
          </div>
          <Table
            data={premedicationData}
            selectedOption={selectedOption}
            checkedRows={checkedRows}
            handleCheckboxChange={handleCheckboxChange}
          />
        </div>
        <div className="chemotherapy">
          <div className="chemotherapy-header">
            <p className="table-name">Chemotherapy</p>
            <div className="buttons-container">
              <button className="dose btn">Change Dose</button>
              <button className="edit btn">Edit</button>
              <button className="del btn" onClick={handleDeleteCheckedRows}>
                Delete Checked Rows
              </button>
            </div>
          </div>
          <Table
            data={chemotherapyData}
            selectedOption={selectedOption}
            checkedRows={checkedRows}
            handleCheckboxChange={handleCheckboxChange}
          />
        </div>
      </div>
      <div className="notes">
        <span className="heading">Physician Notes</span>
        <textarea
          value={notes}
          className="notes-area"
          onChange={(e) => setNotes(e.target.value)}
        ></textarea>
        <button className="next" onClick={handleNextClick}>
          Next
        </button>
      </div>
    </div>
  );
}
