// export default Table;

// Table component
function Table({ data, selectedOption, onDeleteRow }) {
  const [checkedItems, setCheckedItems] = useState({});

  useEffect(() => {
    setCheckedItems({});
  }, [selectedOption]);

  const handleCheckboxChange = (index) => {
    setCheckedItems((prevCheckedItems) => ({
      ...prevCheckedItems,
      [index]: !prevCheckedItems[index],
    }));
  };

  const handleDeleteRow = (index) => {
    const newData = data[selectedOption].filter((item, i) => i !== index);
    onDeleteRow(selectedOption, newData);
    setCheckedItems((prevCheckedItems) => {
      const updatedCheckedItems = { ...prevCheckedItems };
      delete updatedCheckedItems[index];
      return updatedCheckedItems;
    });
  };

  return (
    <table className="medictions-table">
      <thead>
        <tr>
          <th className="checkboxCell"></th>
          <th style={{ width: "240px" }}>Mediaction</th>
          <th style={{ width: "130px" }}>Dose</th>
          <th style={{ width: "160px" }}>Route</th>
          <th>Instructions</th>
          <th></th> {/* New column for delete button */}
        </tr>
      </thead>
      <tbody>
        {selectedOption &&
          data[selectedOption].map((item, index) => (
            <tr key={index}>
              <td className="checkboxCell">
                <input
                  type="checkbox"
                  checked={checkedItems[index] || false}
                  onChange={() => handleCheckboxChange(index)}
                />
              </td>
              {Object.values(item).map((value, colIndex) => (
                <td key={colIndex}>{value}</td>
              ))}
              <td>
                <button onClick={() => handleDeleteRow(index)}>Delete</button>
              </td>
            </tr>
          ))}
      </tbody>
    </table>
  );
}

// RegimenDetails component
export default function RegimenDetails({ selectedOption }) {
  const [premedicationData, setPremedicationData] = useState({
    /* premedicationData state */
  });
  const [chemotherapyData, setChemotherapyData] = useState({
    /* chemotherapyData state */
  });

  const handleDeleteRow = (category, newData) => {
    if (category === "premedicationData") {
      setPremedicationData((prevData) => ({
        ...prevData,
        [selectedOption]: newData,
      }));
    } else if (category === "chemotherapyData") {
      setChemotherapyData((prevData) => ({
        ...prevData,
        [selectedOption]: newData,
      }));
    }
  };

  return (
    <div className="regimen-detail">
      {/* Other code */}
      <div className="medications">
        <div className="pre-mediaction">
          <div className="pre-mediaction-header">{/* Other code */}</div>
          <Table
            data={premedicationData}
            selectedOption={selectedOption}
            onDeleteRow={handleDeleteRow}
          />
        </div>
        <div className="chemotherapy">
          <div className="chemotherapy-header">{/* Other code */}</div>
          <Table
            data={chemotherapyData}
            selectedOption={selectedOption}
            onDeleteRow={handleDeleteRow}
          />
        </div>
      </div>
      {/* Other code */}
    </div>
  );
}
