import React, { useEffect, useState } from "react";
import "./style.css";

function Table({ data, selectedOption, checkedItems, onCheckboxChange }) {
  // const [checkedItems, setCheckedItems] = useState({});

  // useEffect(() => {
  //   setCheckedItems({});
  // }, [selectedOption]);

  // const handleCheckboxChange = (index) => {
  //   setCheckedItems((prevCheckedItems) => {
  //     const updatedCheckedItems = { ...prevCheckedItems };

  //     // Toggle the checkbox state
  //     updatedCheckedItems[index] = !prevCheckedItems[index];

  //     // Call the callback function with the item data
  //     if (updatedCheckedItems[index]) {
  //       onCheckboxChange(data[selectedOption][index]);
  //     }

  //     return updatedCheckedItems;
  //   });
  // };

  return (
    <table className="medications-table">
      <thead>
        <tr>
          <th className="checkboxCell"></th>
          <th style={{ width: "240px" }}>Mediaction</th>
          <th style={{ width: "130px" }}>Dose</th>
          <th style={{ width: "160px" }}>Route</th>
          <th>Instructions</th>
        </tr>
      </thead>
      <tbody>
        {selectedOption &&
          data[selectedOption].map((item, index) => (
            <tr key={index}>
              <td className="checkboxCell">
                <input
                  type="checkbox"
                  checked={checkedItems[index] || false}
                  onChange={() => onCheckboxChange(index, item)}
                />
              </td>
              {Object.values(item).map((value, colIndex) => (
                <td key={colIndex}>{value}</td>
              ))}
            </tr>
          ))}
      </tbody>
    </table>
  );
}

export default Table;
