import React, { useState } from "react";
import Table from "components/Table/Table";
import "./style.css";
import MiniDropMenu from "components/MiniDropMenu/MiniDropMenu";

const premedicationData = {
  /* your premedication data */
};
const chemotherapyData = {
  /* your chemotherapy data */
};
const cyclesData = {
  /* your cycles data */
};
const Weeks = ["None", "1", "2", "3", "4", "5", "6"];
const Cycles = [1, 2, 3, 4, 5, 6, 7, 8];
const Days = Array.from({ length: 28 }, (_, index) => index + 1);
const fieldsNum = [1, 2, 3];

export default function RegimenDetails({ selectedOption }) {
  const [formData, setFormData] = useState({
    type: "",
    weeks: "",
    cycles: "",
    days: "",
    cycleDays: "",
    numOfCycles: "",
    premedications: [],
    chemotherapies: [],
    notes: "",
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(formData); // This will log the object containing all the data
    // Here you can send the formData object to your backend or wherever it needs to be processed
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  return (
    <div className="regimen-detail">
      <form onSubmit={handleSubmit}>
        <div className="cycles-container">
          <span className="heading">Cycles</span>
          <div className="cyles-rows">{/* Your code for cycles */}</div>
        </div>
        <div className="medications">
          <span className="heading">Medications</span>
          <div className="pre-medication">
            {/* Your code for premedications */}
          </div>
          <div className="chemotherapy">
            {/* Your code for chemotherapies */}
          </div>
        </div>
        <div className="notes">
          <span className="heading">Physician Notes</span>
          <textarea
            defaultValue="Add your notes here..."
            className="notes-area"
            name="notes"
            onChange={handleChange}
          ></textarea>
          <button type="submit" className="next">
            Submit
          </button>
        </div>
      </form>
    </div>
  );
}
