import React, { useState } from "react";
import "./style.css";

function Table({ data, selectedOption }) {
  const [checked, setChecked] = useState();
  const handleChangeCheck = (event) => {
    setChecked(event.target.value);
  };
  return (
    <table className="medictions-table">
      <thead>
        <tr>
          <th className="checkboxCell"></th>
          <th style={{ width: "240px" }}>Mediaction</th>
          <th style={{ width: "130px" }}>Dose</th>
          <th style={{ width: "160px" }}>Route</th>
          <th>Instructions</th>
        </tr>
      </thead>
      <tbody>
        {selectedOption &&
          data[selectedOption].map((item, index) => (
            <tr key={index}>
              <td className="checkboxCell">
                <input type="checkbox" onChange={handleChangeCheck} />
              </td>
              {Object.values(item).map((value, colIndex) => (
                <td key={colIndex}>{value}</td>
              ))}
            </tr>
          ))}
      </tbody>
    </table>
  );
}

export default Table;
