import React, { useState } from "react";
import "./style.css";

function Table({ data, selectedOption }) {
  const [checkedRows, setCheckedRows] = useState([]);

  const handleCheckboxChange = (index) => {
    if (checkedRows.includes(index)) {
      setCheckedRows(checkedRows.filter((rowIndex) => rowIndex !== index));
    } else {
      setCheckedRows([...checkedRows, index]);
    }
  };

  const handleDeleteRows = () => {
    const newData = { ...data };
    checkedRows.forEach((index) => {
      newData[selectedOption] = newData[selectedOption].filter(
        (_, rowIndex) => rowIndex !== index
      );
    });
    setCheckedRows([]);
  };

  return (
    <div>
      <button onClick={handleDeleteRows}>Delete Selected Rows</button>
      <table className="medictions-table">
        <thead>
          <tr>
            <th className="checkboxCell">
              <input type="checkbox" />{" "}
            </th>
            <th style={{ width: "240px" }}>Medication</th>
            <th style={{ width: "130px" }}>Dose</th>
            <th style={{ width: "160px" }}>Route</th>
            <th>Instructions</th>
          </tr>
        </thead>
        <tbody>
          {selectedOption &&
            data[selectedOption].map((item, index) => (
              <tr key={index}>
                <td className="checkboxCell">
                  <input
                    type="checkbox"
                    checked={checkedRows.includes(index)}
                    onChange={() => handleCheckboxChange(index)}
                  />
                </td>
                {Object.values(item).map((value, colIndex) => (
                  <td key={colIndex}>{value}</td>
                ))}
              </tr>
            ))}
        </tbody>
      </table>
    </div>
  );
}

export default Table;
