import MiniDropMenu from "components/MiniDropMenu/MiniDropMenu";
import { useState } from "react";
import "./style.css";

export default function EditPopUp() {
  const [isOpen, setIsOpen] = useState(true);

  const handleClose = () => {
    setIsOpen((open) => !open);
  };

  const handleOverlayClick = (e) => {
    e.stopPropagation();
  };

  return (
    <>
      {isOpen && (
        <div className="edit-popup-overlay" onClick={handleClose}>
          <div className="edit-popup-container" onClick={handleOverlayClick}>
            <div className="popup-heading">
              <div className="text">
                <img
                  src="images/pop-icon.png"
                  alt="icon"
                  width={74}
                  height={74}
                />
                <p>SODUIM CHLORIDE</p>
              </div>
              <div className="x-icon" onClick={handleClose}>
                <img src="images/x.svg" alt="x-icon" />
              </div>
            </div>
            <div className="edit-values">
              <p>Please enter the updated values:</p>
              <table>
                <thead>
                  <tr>
                    <th style={{ width: "150px" }}>Dose</th>
                    <th style={{ width: "180px" }}>Route</th>
                    <th>Instructions</th>
                  </tr>
                </thead>
                <tbody>
                  <tr>
                    <td>
                      <input type="number" className="dose-input" />
                    </td>
                    <td>
                      <MiniDropMenu
                        title="route"
                        options={["Oral", "Intravenous"]}
                      />
                    </td>
                    <td>
                      <input type="text" />
                    </td>
                  </tr>
                </tbody>
              </table>
            </div>
            <div className="buttons">
              <button className="btn cancel" onClick={handleClose}>
                Cancel
              </button>
              <button className="btn save">Save</button>
            </div>
          </div>
        </div>
      )}
    </>
  );
}
