import Table from "components/Table/Table";
import "./style.css";
import MiniDropMenu from "components/MiniDropMenu/MiniDropMenu";
import { useEffect, useState } from "react";

const premedicationData = {
  "COP - Regimen for Non-Metastatic Non Hodgkin Lymphoma": [
    {
      Medication: "SODIUM CHLORIDE",
      Dose: 1000,
      Route: "Intravenous",
      Instructions: "Once prior to chemotherapy",
    },
    {
      Medication: "Ondansetron",
      Dose: 8,
      Route: "Oral",
      Instructions: "Once 60 minutes prior to chemotherapy",
    },
  ],
  "AC - Regimen for Non-Metastatic, Locally-Advanced Breast Cancer. Before or after Taxol":
    [
      {
        Medication: "SODIUM CHLORIDE",
        Dose: 100,
        Route: "Intravenous",
        Instructions: "Once prior to chemotherapy",
      },
      {
        Medication: "Dexamethasone",
        Dose: 16,
        Route: "Oral",
        Instructions: "Once 60 minutes prior to chemotherapy",
      },
      {
        Medication: "Ondansetron",
        Dose: 8,
        Route: "Intravenous",
        Instructions: "Once 60 minutes prior to chemotherapy",
      },
    ],
  "CHOP: Protocol for Non Hodgkin Lymphoma": [
    {
      Medication: "SODIUM CHLORIDE",
      Dose: 100,
      Route: "Oral",
      Instructions: "Once prior to chemotherapy",
    },
    {
      Medication: "Ondansetron",
      Dose: 8,
      Route: "Oral",
      Instructions: "Once 60 minutes prior to chemotherapy",
    },
  ],
  "CMF-Breast Cancer Regimen": [
    {
      Medication: "SODIUM CHLORIDE",
      Dose: 1000,
      Route: "Intravenous",
      Instructions: "Once  minutes prior to chemotherapy",
    },
    {
      Medication: "Dexamethasone",
      Dose: 16,
      Route: "Oral",
      Instructions: "Once 60 minutes prior to chemotherapy",
    },
    {
      Medication: "Ondansetron",
      Dose: 8,
      Route: "Oral",
      Instructions: "Once 60 minutes prior to chemotherapy",
    },
  ],
};
const chemotherapyData = {
  "COP - Regimen for Non-Metastatic Non Hodgkin Lymphoma": [
    {
      Medication: "Prednisone",
      Dose: 100,
      Route: "Oral",
      Instructions: "Daily x 5 days. 1st dose 60 minutes prio to chemotherapy.",
    },
    {
      Medication: "Vincristine",
      Dose: 1.4,
      Route: "Intravenous",
      Instructions: "IV Push over 1-2 minutes",
    },
    {
      Medication: "Cyclophosphamide",
      Dose: 750,
      Route: "Intravenous",
      Instructions: "IV Push over 1-2 hours",
    },
  ],
  "AC - Regimen for Non-Metastatic, Locally-Advanced Breast Cancer. Before or after Taxol":
    [
      {
        Medication: "Doxorubici",
        Dose: 60,
        Route: "Intravenous",
        Instructions: "IV Push over 15 minutes",
      },
      {
        Medication: "Cyclophosphamide",
        Dose: 600,
        Route: "Intravenous",
        Instructions: "Infuse over 1-2 hours",
      },
    ],
  "CHOP: Protocol for Non Hodgkin Lymphoma": [
    {
      Medication: "Prednisone",
      Dose: 100,
      Route: "Oral",
      Instructions: "Daily x 5 days. 1st dose 60 minutes prio to chemotherapy",
    },
    {
      Medication: "Doxorubicin",
      Dose: 50,
      Route: "Oral",
      Instructions: "IV Push over 15 minutes",
    },
    {
      Medication: "Vincristine",
      Dose: 1.4,
      Route: "Oral",
      Instructions: "IV Push over 1-2 minutes",
    },
    {
      Medication: "Cyclophosphamide",
      Dose: 750,
      Route: "Oral",
      Instructions: "IV Push over 1-2 hours",
    },
  ],
  "CMF-Breast Cancer Regimen": [
    {
      Medication: "Fluorouracil",
      Dose: 600,
      Route: "Intravenous",
      Instructions: "IV push over 10 minutes",
    },
    {
      Medication: "Methotrexate",
      Dose: 40,
      Route: "Oral",
      Instructions: "IV push over 10 mg/min",
    },
    {
      Medication: "Cyclophosphamide",
      Dose: 600,
      Route: "Oral",
      Instructions: "Infuse over 1-2 hours",
    },
  ],
};
const cyclesData = {
  "COP - Regimen for Non-Metastatic Non Hodgkin Lymphoma": [
    { NumOfCycles: 3, CycleCount: 2 },
  ],
  "AC - Regimen for Non-Metastatic, Locally-Advanced Breast Cancer. Before or after Taxol":
    [{ NumOfCycles: 8, CycleCount: 5 }],
  "CHOP: Protocol for Non Hodgkin Lymphoma": [
    { NumOfCycles: 2, CycleCount: 3 },
  ],
  "CMF-Breast Cancer Regimen": [{ NumOfCycles: 4, CycleCount: 4 }],
};

const Weeks = ["None", "1", "2", "3", "4", "5", "6"];

const Cycles = [1, 2, 3, 4, 5, 6, 7, 8];

const Days = Array.from({ length: 28 }, (_, index) => index + 1);

const fieldsNum = [1, 2, 3];

export default function RegimenDetails({ selectedOption }) {
  const [notes, setNotes] = useState("Add your notes here...");
  const [cycleType, setCycleType] = useState("weeks");

  useEffect(() => {
    setNotes("Add your notes here...");
    setCycleType("weeks");
  }, [selectedOption]);

  const handleCycleTypeChange = (event) => {
    setCycleType(event.target.value);
  };

  const handleNotesChange = (event) => {
    setNotes(event.target.value);
  };
  useEffect(() => {
    setNotes("Add your notes here...");
  }, [selectedOption]);
  const [regimenDetails, setRegimenDetails] = useState({
    regimenName: selectedOption,
    preMedication: [],
    chemoTherapy: [],
    physicianNotes: notes,
  });

  useEffect(() => {
    setNotes("Add your notes here...");
    setRegimenDetails({
      ...regimenDetails,
      regimenName: selectedOption,
    });
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [selectedOption]);

  const handleNextClick = () => {
    let dayCycleInputs = [];
    let cycleDetails;

    if (cycleType === "weeks") {
      cycleDetails = {
        CycleCount: cyclesData[selectedOption][0].CycleCount,
        NumOfCycles: cyclesData[selectedOption][0].NumOfCycles,
      };
    } else {
      cycleDetails = {
        CycleCount: cyclesData[selectedOption][0].CycleCount,
        DayCycle: cyclesData[selectedOption][0].DayCycle || 1,
        Days: cyclesData[selectedOption][0].Days || 1,
        NumOfCycles: cyclesData[selectedOption][0].NumOfCycles,
        DayCycleInputs: dayCycleInputs, // Include the day cycle inputs in the object
      };
    }
    const newRegimenDetails = {
      regimenName: selectedOption,
      preMedication: premedicationData[selectedOption] || [],
      chemoTherapy: chemotherapyData[selectedOption] || [],
      physicianNotes: notes,
      ...cycleDetails,
    };

    setRegimenDetails(newRegimenDetails);

    setRegimenDetails((prevRegimenDetails) => {
      console.log(prevRegimenDetails);
      return prevRegimenDetails;
    });
  };
  useEffect(() => {
    const script = document.createElement("script");
    script.type = "text/javascript";
    script.src = "https://assets.calendly.com/assets/external/widget.js";
    document.body.appendChild(script);

    script.onload = () => {
      Calendly.initInlineWidget({
        url: "https://calendly.com/YOURLINK",
        parentElement: document.querySelector(".calendly-inline-widget"),
      });
    };

    return () => {
      document.body.removeChild(script);
    };
  }, []);

  return (
    <div
      className="calendly-inline-widget"
      style={{ minWidth: 320, height: 580 }}
      data-auto-load="false"
    ></div>
  );

  return (
    <div className="regimen-detail">
      <div className="cycles-container">
        <span className="heading">Cycles</span>
        <div className="cyles-rows">
          <form>
            {" "}
            <div className="cycles-row">
              <input
                type="radio"
                name="type"
                value="weeks"
                checked={cycleType === "weeks"}
                onChange={handleCycleTypeChange}
              />
              <span>Every</span>
              <MiniDropMenu
                title="Weeks"
                options={Weeks}
                defaultValue={cyclesData[selectedOption][0].CycleCount}
              />
              <span>X</span>
              <MiniDropMenu
                title="Cycles"
                options={Cycles.map(String)}
                defaultValue={cyclesData[selectedOption][0].NumOfCycles}
              />
              <span>Cycles</span>
            </div>
            <div className="cycles-row">
              <input
                type="radio"
                name="type"
                value="days"
                checked={cycleType === "days"}
                onChange={handleCycleTypeChange}
              />
              <span style={{ width: "40px" }}>Day</span>
              <MiniDropMenu title="fieldsNum" options={fieldsNum.map(String)} />

              <span>of</span>
              <MiniDropMenu title="Days" options={Days.map(String)} />
              <span>day cycle</span>
              <span>X</span>
              <MiniDropMenu
                title="Cycles"
                options={Cycles.map(String)}
                defaultValue={cyclesData[selectedOption][0].NumOfCycles}
              />
              <span>Cycles</span>
            </div>
          </form>
        </div>
      </div>
      <div className="medications">
        <span className="heading">Mediactions</span>
        <div className="pre-mediaction">
          <div className="pre-mediaction-header">
            <p className="table-name">PreMediactions</p>
            <div className="buttons-container">
              <button className="edit btn">Edit</button>
              <button className="del btn">Delete</button>
            </div>
          </div>
          <Table data={premedicationData} selectedOption={selectedOption} />
        </div>
        <div className="chemotherapy">
          <div className="chemotherapy-header">
            <p className="table-name">ChemoTherapy</p>
            <div className="buttons-container">
              <button className="dose btn">Change Dose</button>
              <button className="edit btn">Edit</button>
              <button className="del btn">Delete</button>
            </div>
          </div>
          <Table data={chemotherapyData} selectedOption={selectedOption} />
        </div>
      </div>
      <div className="notes">
        <span className="heading">Physician Notes</span>
        <textarea
          value={notes}
          className="notes-area"
          onChange={handleNotesChange}
        ></textarea>
        <button className="next" onClick={handleNextClick}>
          Next
        </button>
      </div>
    </div>
  );
}
