import { useRef, useState } from "react";
import "./style.css";

export default function DosePopUp({ onClose, selectedItemData }) {
  const DosePopUpRef = useRef();
  const [value, setValue] = useState(0);
  const values = ["0%", "-10%", "-20%", "-30%", "-40%", "-50%"];

  const handleChange = (e) => {
    setValue(parseInt(e.target.value));
  };

  //   const calculatePosition = (value) => {
  //     const index = parseInt(value);
  //     const percent = (index / (values.length - 1)) * 100;
  //     return `calc(${percent}% )`;
  //   };
  const closeDosePopUp = (e) => {
    if (DosePopUpRef.current === e.target) {
      onClose();
    }
  };
  return (
    <div
      className="dose-popup-overlay"
      ref={DosePopUpRef}
      onClick={closeDosePopUp}
    >
      <div className="dose-popup-container">
        <div className="popup-heading">
          <div className="text">
            <img src="images/pop-icon.png" alt="icon" width={74} height={74} />
            <p>Change Dosage</p>
          </div>
          <div className="x-icon" onClick={onClose}>
            <img src="images/x.svg" alt="x-icon" />
          </div>
        </div>
        <div className="dose-data">
          <div className="data">
            <p>{selectedItemData.Medication}</p>
            <span>{selectedItemData.Instructions}</span>
          </div>
          <span>No reduction</span>
        </div>
        <div className="dose-values">
          <img src="images/reduce icon.svg" alt="icon" width={14} height={14} />
          <p>Reduce the dosage by</p>
        </div>
        <div className="range-container">
          <input
            type="range"
            min="0"
            max="5"
            value={value}
            onChange={handleChange}
            step="1"
            className="dose-range"
          />
          {values.map((val, index) => (
            <div key={index}>
              <span className="circle" style={{ left: "10px" }} />
              <span
                style={{
                  position: "absolute",
                  left: calculatePosition(index),
                  bottom: "-15px",
                  fontSize: "12px",
                  whiteSpace: "nowrap",
                }}
              >
                {val}
              </span>
            </div>
          ))}
        </div>

        <div className="buttons">
          <button className="btn cancel" onClick={onClose}>
            Cancel
          </button>
          <button className="btn save">Save</button>
        </div>
      </div>
    </div>
  );
}
