import React, { createContext, useState, useContext } from "react";

const RegimenContext = createContext();

export const RegimenProvider = ({ children }) => {
  const [regimenDetails, setRegimenDetails] = useState(null);

  return (
    <RegimenContext.Provider value={{ regimenDetails, setRegimenDetails }}>
      {children}
    </RegimenContext.Provider>
  );
};

export const useRegimen = () => useContext(RegimenContext);
