import MiniDropMenu from "components/MiniDropMenu/MiniDropMenu";
import { useRef, useState } from "react";
import "./style.css";

export default function EditPopUp({ onClose }) {
  const EditPopUpRef = useRef();

  const closeEditPopUp = (e) => {
    if (EditPopUpRef.current === e.target) {
      onClose();
    }
  };
  return (
    <>
      {/* {isOpen && ( */}
      <div
        className="edit-popup-overlay"
        ref={EditPopUpRef}
        onClick={closeEditPopUp}
      >
        <div className="edit-popup-container">
          <div className="popup-heading">
            <div className="text">
              <img
                src="images/pop-icon.png"
                alt="icon"
                width={74}
                height={74}
              />
              <p>SODUIM CHLORIDE</p>
            </div>
            <div className="x-icon" onClick={onClose}>
              <img src="images/x.svg" alt="x-icon" />
            </div>
          </div>
          <div className="edit-values">
            <p>Please enter the updated values:</p>
            <table>
              <thead>
                <tr>
                  <th style={{ width: "150px" }}>Dose</th>
                  <th style={{ width: "180px" }}>Route</th>
                  <th>Instructions</th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <td>
                    <input type="number" className="dose-input" />
                  </td>
                  <td>
                    <MiniDropMenu
                      title="route"
                      options={["Oral", "Intravenous"]}
                    />
                  </td>
                  <td>
                    <input type="text" />
                  </td>
                </tr>
              </tbody>
            </table>
          </div>
          <div className="buttons">
            <button className="btn cancel" onClick={onClose}>
              Cancel
            </button>
            <button className="btn save">Save</button>
          </div>
        </div>
      </div>

      {/* )} */}
    </>
  );
}
