import React, { useState } from "react";
import "./style.css";
import RegimenDetails from "redimenDetails/RegimenDetails";

const Weeks = [
  "None",
  "1 Weeks",
  "2 Weeks",
  "3 Weeks",
  "4 Weeks",
  "5 Weeks",
  "6 Weeks",
];
// const cycles = [1, 2, 3, 4, 5, 6];
// const days = Array.from({ length: 28 }, (_, index) => index + 1);
export default function MiniDropMenu() {
  const [isOpen, setIsOpen] = useState(false);
  const [selectedOption, setSelectedOption] = useState("none");

  const toggleDropdown = () => {
    setIsOpen(!isOpen);
  };

  const handleOptionSelect = (option) => {
    setSelectedOption(option);
    setIsOpen(false);
  };

  return (
    <div className="regimens-details-container">
      <div className="dropdown-container">
        <div
          className={`dropdown-header ${isOpen ? "open" : ""}`}
          onClick={toggleDropdown}
        >
          <span>{selectedOption === "none" ? "None" : selectedOption}</span>

          <span className={`arrow ${isOpen ? "up" : "down"}`}></span>
        </div>
        {isOpen && (
          <div className="dropdown-list">
            {Weeks.map((week, index) => (
              <div
                key={index}
                className="dropdown-option"
                onClick={() => handleOptionSelect(week)}
              >
                {week}
              </div>
            ))}
          </div>
        )}
      </div>
      {selectedOption !== "none" && (
        <RegimenDetails selectedOption={selectedOption} />
      )}
    </div>
  );
}
