import Table from "components/Table/Table";
import "./style.css";
import { useEffect, useRef, useState } from "react";
const data = [
  {
    Medication: "SODIUM CHLORIDE",
    Dose: 1000,
    Route: "Intravenous",
    Instructions: "Once 60 minutes prior to chemotherapy",
  },
  {
    Medication: "SODIUM CHLORIDE",
    Dose: 1000,
    Route: "Intravenous",
    Instructions: "Once 60 minutes prior to chemotherapy",
  },
  {
    Medication: "SODIUM CHLORIDE",
