    Dose: 1000,
    Route: "Intravenous",
    Instructions: "Once 60 minutes prior to chemotherapy",
  },
  {
    Medication: "SODIUM CHLORIDE",
    Dose: 1000,
    Route: "Intravenous",
    Instructions: "Once 60 minutes prior to chemotherapy",
  },
  {
    Medication: "SODIUM CHLORIDE",
    Dose: 1000,
    Route: "Intravenous",
    Instructions: "Once 60 minutes prior to chemotherapy",
  },
];

export default function RegimenDetails() {
  const premedicationRef = useRef(null);
  const chemotherapyRef = useRef(null);
  const [premedicationHeight, setPremedicationHeight] = useState(0);

  useEffect(() => {
    if (premedicationRef.current) {
      setPremedicationHeight(premedicationRef.current.offsetHeight);
    }
  }, []);

  return (
    <>
      <div className="cycles">
        <span className="heading">Cycles</span>
        <div className="row">
          <input type="radio" />
          <span>Every</span>
        </div>
        <div className="row">
          <input type="radio" />
          <span>Day</span>
        </div>
        <button>Check</button>
      </div>
      <div className="medications">
        <span className="heading">Mediactions</span>
        <div className="premediaction" ref={premedicationRef}>
          <div className="premediactionTable">
            <p>PreMediactions</p>
            <div className="prebuttons">
              <button className="edit">Edit</button>
              <button className="del">Delete</button>
            </div>
          </div>
          <Table data={data} />
        </div>
        <div
          className="chemotherapy"
          ref={chemotherapyRef}
          style={{ top: premedicationHeight + 50 }}
        >
          <p>ChemoTherapy</p>
        </div>
      </div>
      <div className="notes">
        <span className="heading">Physician Notes</span>
        <input type="text" placeholder="Add your notes here..." />
      </div>
    </>
  );
}
