import React, { useState, useEffect } from "react";
import "./style.css";

function Table({ data, selectedOption, checkedItems, onCheckboxChange }) {
  const [tableData, setTableData] = useState(data[selectedOption] || []);

  useEffect(() => {
    setTableData(data[selectedOption] || []);
  }, [data, selectedOption]);

  return (
    <table className="medications-table">
      <thead>
        <tr>
          <th className="checkboxCell"></th>
          <th style={{ width: "240px" }}>Medication</th>
          <th style={{ width: "130px" }}>Dose</th>
          <th style={{ width: "160px" }}>Route</th>
          <th>Instructions</th>
        </tr>
      </thead>
      <tbody>
        {tableData.map((item, index) => (
          <tr key={index}>
            <td className="checkboxCell">
              <input
                type="checkbox"
                checked={checkedItems[index] || false}
                onChange={() => onCheckboxChange(index, item)}
              />
            </td>
            {Object.values(item).map((value, colIndex) => (
              <td key={colIndex}>{value}</td>
            ))}
          </tr>
        ))}
      </tbody>
    </table>
  );
}

export default Table;
