const RegimenContext = createContext();

export const RegimenProvider = ({ children }) => {
  const [Data, setData] = useState(apiData); // Initialize with your initial data
  // Other context-related logic...

  return (
    <RegimenContext.Provider value={{ Data, setData }}>
      {children}
