import React, { useState } from "react";
import "./style.css";

function Table({ data, selectedOption, onRowDelete }) {
  const [checkedRows, setCheckedRows] = useState([]);

  const handleCheckboxChange = (index) => {
    const isChecked = checkedRows.includes(index);

    if (isChecked) {
      setCheckedRows(checkedRows.filter((rowIndex) => rowIndex !== index));
    } else {
      setCheckedRows([...checkedRows, index]);
    }
  };

  const handleDeleteClick = () => {
    onRowDelete(checkedRows);
    setCheckedRows([]);
  };

  return (
    <table className="medications-table">
      <thead>
        <tr>
          <th className="checkboxCell">
            <input
              type="checkbox"
              onChange={() =>
                setCheckedRows(
                  Array.from({ length: data[selectedOption].length }, (_, i) =>
                    checkedRows.includes(i) ? null : i
                  ).filter((index) => index !== null)
                )
              }
            />
          </th>
          <th style={{ width: "240px" }}>Medication</th>
          <th style={{ width: "130px" }}>Dose</th>
          <th style={{ width: "160px" }}>Route</th>
          <th>Instructions</th>
        </tr>
      </thead>
      <tbody>
        {selectedOption &&
          data[selectedOption].map((item, index) => (
            <tr key={index}>
              <td className="checkboxCell">
                <input
                  type="checkbox"
                  checked={checkedRows.includes(index)}
                  onChange={() => handleCheckboxChange(index)}
                />
              </td>
              {Object.values(item).map((value, colIndex) => (
                <td key={colIndex}>{value}</td>
              ))}
            </tr>
          ))}
      </tbody>
      <tfoot>
        <tr>
          <td className="checkboxCell">
            <button onClick={handleDeleteClick}>Delete</button>
          </td>
          <td colSpan="4"></td>
        </tr>
      </tfoot>
    </table>
  );
}

export default Table;
